mapper.select:
select dish.id, dish.name, dish.category_id, dish.price, dish.image, dish.description, dish.status, dish.create_time, dish.update_time, dish.create_user, dish.update_user, category.name categoryName from dish, category
dish.category_id = category.id and dish.name like concat('%',#{name}, '%') and dish.category_id = #{categoryId} and dish.status = #{status}
order by create_time desc
