mapper.select:
select *
from shopping_cart

    id=#{id}
    and name=#{name}
    and user_id=#{userId}
    and dish_id=#{dishId}
    and setmeal_id=#{setmealId}
    and dish_flavor=#{dishFlavor}
    and number=#{number}
    and amount=#{amount}
    and image=#{image}
    and create_time=#{createTime}

order by create_time desc

mapper.delete:
delete from shopping_cart

    user_id = #{userId}
    and dish_id =#{dishId}
    and setmeal_id =#{setmealId}
    and dish_flavor =#{dishFlavor}
